const Search = (props) => {

    callback = (term ) => {
        this.setState({
               term: term 
            })
      }
    filterIt = (book, searchTerm) => {
        return book.title.includes(searchTerm)
        } 

    return (
      <>
      <form>
        <input type="text"
          placeholder="Recherche"
          onChange={(event)=>props.callback(event.target.value)}
        />
      </form>
      </>
    )}
export default Search;
