import React, { Component } from 'react';
import Arnold from './../components/arnold';
import Avengers from './../components/avengers';
import Rambo from './../components/rambo';

class List extends Component {


  film = [{
    titre: ['Arnold','Avengers','RAMBO 4','Venum','Capitain America']
  }]

    render() {

        return (

          <div className="App">

          <input id="recherche" className="form-control"  type="text" placeholder="Recherche"  onChange={this.onchange}/>
           <Arnold id="arnold"/>
           <Avengers id="avengers"/>
           <Rambo id="rambo"/>
            <div className="card" onClick={()=>{
              document.getElementById("arnold").style.display = "block";
              document.getElementById("total").style.display = "none";

            }
              }>
                <img className="card-img-top" src="images/arnold.jpeg" alt="Card image"/><br/>

                <div className="card-body">
                  <h4 className="card-title">{this.film.map(item =>{return (item.titre[0])})}</h4>
                </div>
            </div>

            <div className="card" onClick={()=>{
              document.getElementById("avengers").style.display = "block"
            }} id="total">
                <img className="card-img-top" src="images/avengers.jpeg" alt="Card image"/><br/>

                <div className="card-body">
                  <h4 className="card-title">Avengers</h4>
                </div>
            </div>

            <div className="card" onClick={()=>{
              document.getElementById("rambo").style.display = "block"
            }} id="total">
                <img className="card-img-top" src="images/rambo.jpeg" alt="Card image"/><br/>

                <div className="card-body">
                  <h4 className="card-title">Rambo</h4>

                </div>
            </div>
            <div className="card" onClick={()=>{
              document.getElementById("rambo").style.display = "block"
            }} id="total">
                <img className="card-img-top" src="images/venum.jpeg" alt="Card image"/><br/>

                <div className="card-body">
                  <h4 className="card-title">Venum</h4>
                </div>
            </div>
            <div className="card">
                <img className="card-img-top" src="images/capitain.jpeg" alt="Card image"/><br/>

                <div className="card-body">
                  <h4 className="card-title">Capitain America</h4>
                </div>
            </div>
            <div className="card">
                <img className="card-img-top" src="images/avengers.jpeg" alt="Card image"/><br/>

                <div className="card-body">
                  <h4 className="card-title">Avengers</h4>
                </div>
            </div>
          </div>

        );
      }
}
export default List;
