import React, { Component } from 'react';
import icons from 'glyphicons'
import { voteReact} from './../Action/action'

class List extends Component {
  constructor(props) {
    super(props);
    this.store = this.props.store;
  }
  handleVoteReact = () => {
    this.store.dispatch(voteReact());
  }
  callback = (term ) => {
    this.setState({
           term: term 
        })
  }
  film = [{
    titre: ['Arnold','Avengers','RAMBO 4','Venum','Capitain America']
  }]

    render() {
        return (
          <div className="App">
            <div className="card">
                <img className="card-img-top" src="images/arnold.jpeg" alt="Card image"/><br/>
                <span onClick={this.handleVoteReact}>{icons.heart}</span>
                <div className="card-body">
                  <h4 className="card-title">{this.film.map(item =>{return (item.titre[0])})}</h4>
                  <p className="card-text">Le reducers vous permet de gérer la mise à jour des données. Le reducers et les actions sont complémentaires. Les traitements comme les requêtes sont réalisées dans le fichier
                   actions.js et le reducers s’occupent de mettre à jour les données du store (state global).</p>
                </div>
            </div>
            <div className="card">
                <img className="card-img-top" src="images/avengers.jpeg" alt="Card image"/><br/>
                {icons.heart}
                <div className="card-body">
                  <h4 className="card-title">{this.film.map(item =>{return (item.titre[1])})}</h4>
                  <p className="card-text">Le reducers vous permet de gérer la mise à jour des données. Le reducers et les actions sont complémentaires. Les traitements comme les requêtes sont réalisées dans le fichier
                   actions.js et le reducers s’occupent de mettre à jour les données du store (state global).</p>
                </div>
            </div>
            <div className="card">
                <img className="card-img-top" src="images/rambo.jpeg" alt="Card image"/><br/>
                {icons.heart}
                <div className="card-body">
                  <h4 className="card-title">{this.film.map(item =>{return (item.titre[2])})}</h4>
                  <p className="card-text">Le reducers vous permet de gérer la mise à jour des données. Le reducers et les actions sont complémentaires. Les traitements comme les requêtes sont réalisées dans le fichier
                   actions.js et le reducers s’occupent de mettre à jour les données du store (state global).</p>
                </div>
            </div>
            <div className="card">
                <img className="card-img-top" src="images/venum.jpeg" alt="Card image"/><br/>
                {icons.heart}
                <div className="card-body">
                  <h4 className="card-title">{this.film.map(item =>{return (item.titre[3])})}</h4>
                  <p className="card-text">Le reducers vous permet de gérer la mise à jour des données. Le reducers et les actions sont complémentaires. Les traitements comme les requêtes sont réalisées dans le fichier
                   actions.js et le reducers s’occupent de mettre à jour les données du store (state global).</p>
                </div>
            </div>
            <div className="card">
                <img className="card-img-top" src="images/capitain.jpeg" alt="Card image"/><br/>
                {icons.heart}
                <div className="card-body">
                  <h4 className="card-title">{this.film.map(item =>{return (item.titre[4])})}</h4>
                  <p className="card-text">Le reducers vous permet de gérer la mise à jour des données. Le reducers et les actions sont complémentaires. Les traitements comme les requêtes sont réalisées dans le fichier
                   actions.js et le reducers s’occupent de mettre à jour les données du store (state global).</p>
                </div>
            </div>
            <div className="card">
                <img className="card-img-top" src="images/avengers.jpeg" alt="Card image"/><br/>
                {icons.heart}
                <div className="card-body">
                  <h4 className="card-title">{this.film.map(item =>{return (item.titre[1])})}</h4>
                  <p className="card-text">Le reducers vous permet de gérer la mise à jour des données. Le reducers et les actions sont complémentaires. Les traitements comme les requêtes sont réalisées dans le fichier
                   actions.js et le reducers s’occupent de mettre à jour les données du store (state global).</p>
                </div>
            </div>
          </div>
        );
      }
}
export default List;
