import React, { Component } from 'react';

class Rechercher extends Component {
    callback = (term ) => {
        this.setState({
               term: term 
            })
      }

    filterIt = (book, searchTerm) => {
        return book.title.includes(searchTerm) || book.author.includes(searchTerm) || book.description.includes(searchTerm)
    }

    render() {
      return (
        <div className="recherche">
            <input id="recherche" className="form-control"  type="text" placeholder="Recherche"  onChange={(event)=>props.callback(event.target.value)}/>
        </div>
      );
    }
  }

  export default Rechercher;
