import React, { Component } from 'react';

class Rechercher extends Component {
    callback = (term ) => {
        this.setState({
               term: 'Arnold' 
            })
      }
      filterIt = (book, searchTerm) => {
        return book.title.includes(searchTerm)
        } 
    render() {
        books.filter(this.filterIt(this.state.term)).map(book => {return (<UserBookCard
            key={book.id}
            book={book}
            deleteUserBook={this.props.deleteUserBook}
            userId={this.props.user}
            addBookNote={this.props.addBookNote} />)
          }
      )
      return (
        <div className="recherche">
            <input id="recherche" className="form-control"  type="text" placeholder="Recherche"  onChange={(event)=>this.props.callback(event.target.value)}/>
        </div>
      );
    }
  }

  export default Rechercher;
